mapper.select:
select

from t_coupon_code




  order by ${example.orderByClause}

LIMIT #{limit} OFFSET #{offset}

SELECT
  tcc.*,tc.`name` AS couponName
FROM t_coupon_code tcc
RIGHT JOIN t_coupon_user tcu ON tcu.coupon_code_id = tcc.id
INNER JOIN t_coupon tc ON tc.id = tcc.coupon_id
WHERE 1=1

    AND tcu.user_open_id = #{openId}


    AND tcc.used_flag = #{usedFlag}


    AND tc.id = #{couponId}

ORDER BY tcc.used_flag ASC,tcc.bind_time DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(tcc.id)
FROM t_coupon_code tcc
RIGHT JOIN t_coupon_user tcu ON tcu.coupon_code_id = tcc.id
INNER JOIN t_coupon tc ON tc.id = tcc.coupon_id
WHERE 1=1

    AND tcu.user_open_id = #{openId}


    AND tcc.used_flag = #{usedFlag}


    AND tc.id = #{couponId}
